import { useState, useEffect } from "react";

function Hello() {
  useEffect(() => {
    console.log("created :>");
    return () => console.log("destroyed :<");
  },[]);
  return (
    <h3>Hello !</h3>
  )
}

function App() {
  const [showing, setShowing] = useState(false);
  const onClick = () => {
    setShowing(!showing);
  }
  return (
    <div>
      <button onClick={onClick}>{showing ? "hide" : "show"}</button>
      {showing ? <Hello /> : null}
    </div>
  );
}

export default App;
