import { useState, useEffect } from "react";

function App() {
  const [isLoading, setLoading] = useState(true);
  const [movies, setMovies] = useState([]);
  useEffect(() => {
    (async ()=>{
      const json = await (await fetch("https://yts.mx/api/v2/list_movies.json?minimum_rating=7&sort_by=year"))
        .json();
      setMovies(json.data.movies);
      setLoading(false);
    })();
  }, []);
  return (
    <div>
      <h1>Movie {movies.length > 0 ? ` : ${movies.length}` : ""}</h1>
      {isLoading ? (
        <div>Loading ...</div>
      ) : (
        <ul>
          {movies.map((movie) => (
            <li key={movie.id}>
              {movie.title} ({movie.year}) [{movie.rating}/10]
            </li>
          ))}
        </ul>
      )}
    </div>
  );
}

export default App;
